from collections import Counter

# Complete the isValid function below.
def isValid(s):
    cnt_char = Counter(s)
    cnt_cnt = Counter([v for k, v in cnt_char.items()])
    if len(cnt_cnt) <= 1:
        return "YES"
    if len(cnt_cnt) == 2:
        a, b = cnt_cnt.keys()
        if (abs(a-b) == 1) and (cnt_cnt[max(a, b)] == 1):
            return "YES"        
        if cnt_cnt[1] == 1:
            return "YES"
    return "NO"

if __name__ == '__main__':
    result = []
    for s in ["aabbcd", "aabbccddeefghi", "abcdefghhgfedecba", ""]:
        result.append(isValid(s))
    print(result)
